import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './css/Category.css'; // Assurez-vous d'avoir le bon chemin vers votre fichier CSS

function Category() {
    const categories = [
        { name: 'Category 1', price: 50 },
        { name: 'Category 2', price: 40 },
        { name: 'Category 3', price: 30 },
    ];
    const [ticketQuantities, setTicketQuantities] = useState(categories.map(() => 0));

    const handleNumTicketsChange = (index, event) => {
        const newQuantities = [...ticketQuantities];
        newQuantities[index] = Math.min(parseInt(event.target.value, 10), 6); // Limite le nombre de billets à 6
        setTicketQuantities(newQuantities);
    };

    return (
        <div className="container">
            <h2>Category Selection Page</h2>
            {categories.map((category, index) => (
                <div key={category.name} className="category-container">
                    <span>{category.name} - Price: ${category.price} - </span>
                    <label htmlFor={`numTickets-${index}`} className="label">Quantity : </label>
                    <input
                        type="number"
                        id={`numTickets-${index}`}
                        value={ticketQuantities[index]}
                        onChange={(event) => handleNumTicketsChange(index, event)}
                        className="input"
                        min="0"
                        max="6"
                    />
                    <span>  Subtotal : ${category.price * ticketQuantities[index]}</span>
                </div>
            ))}
            <p className="total-price">Total Price: ${categories.reduce((acc, category, index) => acc + (category.price * ticketQuantities[index]), 0)}</p>
            <Link to="/confirmation" className="confirmation-button">Confirm</Link>
        </div>
    );
}

export default Category;
