import React, { createContext, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './Login';
import Singup from './Singup';
import Payement from './Payement';
import ForgotPassword from "./ForgotPassword";
import Ticket from "./Ticket";
import Category from "./Category";
import Guest from './Guest';
import Reset from './Reset';
import WelcomeScreen from './WelcomeScreen';
import Success from './Success';
import TicketQuantityPage from './TicketQuantityPage';
import ConfirmationPage from './ConfirmationPage';
import  DoorsPage from './DoorsPage';
import AuthGuard from './Authguard';
import Layout from './Layout';
export const RecoveryContext = createContext();

function App() {

  const [qrCode, setQrCode] = useState(null);

  return (

    <Router>
      <Routes>
        <Route path='/' element={<Login />} />
        <Route path='/singup' element={<Singup />} />
        <Route path='/payement' element={            <AuthGuard> <Payement setQrCode={setQrCode}/></AuthGuard>} />
        <Route path='/ForgotPassword' element={ <ForgotPassword />} />
        <Route path='/Ticket' element={<AuthGuard><Layout> <Ticket /></Layout></AuthGuard> } />
        <Route path='/Category' element={<AuthGuard> <Category /> </AuthGuard>} />
        <Route path='/Guest' element={<AuthGuard> <Guest /></AuthGuard>} />
        <Route path='/Reset' element={<AuthGuard> <Reset /></AuthGuard>} />
        <Route path='/WelcomeScreen' element={<AuthGuard> <Layout><WelcomeScreen /></Layout></AuthGuard>} />
        <Route path='/TicketQuantityPage' element={<AuthGuard> <TicketQuantityPage /></AuthGuard>} />
        <Route path='/WorldCupTicket' element={<AuthGuard> <ConfirmationPage /></AuthGuard>} />
        <Route path="/success" element={<AuthGuard> <Success qrCode={qrCode} /></AuthGuard>} />
        <Route path="/DoorsPage" element={< DoorsPage />} />

      </Routes>
    </Router>

  );
}

export default App;
