import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './css/Admin.css';

function AdminStadiums() {  const { stadiumId } = useParams();
const [stadium, setStadium] = useState(null);
const [formData, setFormData] = useState({});
const navigate = useNavigate();

useEffect(() => {
  axios.get(`http://localhost:5000/api/stadiums/${stadiumId}`)
    .then(response => {
      setStadium(response.data);
      setFormData(response.data);
    })
    .catch(error => {
      console.error('Error fetching stadium:', error);
    });
}, [stadiumId]);

const handleInputChange = (e) => {
  const { name, value } = e.target;
  setFormData({
    ...formData,
    [name]: value,
  });
};

const handleUpdate = () => {
  axios.put(`http://localhost:5000/api/stadiums/${stadiumId}`, formData)
    .then(response => {
      setStadium(response.data);
      alert('Stadium updated successfully');
    })
    .catch(error => {
      console.error('Error updating stadium:', error);
    });
};

const handleDelete = () => {
  axios.delete(`http://localhost:5000/api/stadiums/${stadiumId}`)
    .then(() => {
      alert('Stadium deleted successfully');
      navigate('/admin/stadiums');
    })
    .catch(error => {
      console.error('Error deleting stadium:', error);
    });
};

if (!stadium) {
  return <div>Loading...</div>;
}

return (
  <div className="stadium-detail">
    <h2>Edit Stadium</h2>
    <form>
      <label>
        Name:
        <input type="text" name="name" value={formData.name} onChange={handleInputChange} />
      </label>
      <label>
        Location:
        <input type="text" name="location" value={formData.location} onChange={handleInputChange} />
      </label>
      <label>
        Path:
        <input type="text" name="path" value={formData.path} onChange={handleInputChange} />
      </label>
      <label>
        Categories:
        <textarea name="categories" value={JSON.stringify(formData.categories, null, 2)} onChange={handleInputChange} rows="5" />
      </label>
    </form>
    <button onClick={handleUpdate}>Update Stadium</button>
    <button onClick={handleDelete} style={{ backgroundColor: '#cd2828' }}>Delete Stadium</button>
  </div>
);
}

export default AdminStadiums;
