import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './css/Admin.css';

function AdminMatches() {
  const [matches, setMatches] = useState([]);
  const [editingMatch, setEditingMatch] = useState(null);
  const [newMatch, setNewMatch] = useState({
    name: '',
    away_team_country: '',
    home_team_country: '',
    date: '',
    stade: ''
  });

  useEffect(() => {
    axios.get('http://localhost:5000/api/matches/all')
      .then(response => {
        setMatches(response.data);
      })
      .catch(error => {
        console.error('Error fetching matches:', error);
      });
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (editingMatch) {
      setEditingMatch({ ...editingMatch, [name]: value });
    } else {
      setNewMatch({ ...newMatch, [name]: value });
    }
  };

  const handleSave = () => {
    if (editingMatch) {
      axios.put(`http://localhost:3000/api/matches/${editingMatch._id}`, editingMatch)
        .then(response => {
          setMatches(matches.map(match => (match._id === editingMatch._id ? response.data : match)));
          setEditingMatch(null);
        })
        .catch(error => {
          console.error('Error updating match:', error);
        });
    } else {
      axios.post('http://localhost:3000/api/matches', newMatch)
        .then(response => {
          setMatches([...matches, response.data]);
          setNewMatch({
            name: '',
            away_team_country: '',
            home_team_country: '',
            date: '',
            stade: ''
          });
        })
        .catch(error => {
          console.error('Error creating match:', error);
        });
    }
  };

  const handleEdit = (match) => {
    setEditingMatch(match);
  };

  const handleDelete = (matchId) => {
    axios.delete(`http://localhost:3000/api/matches/${matchId}`)
      .then(() => {
        setMatches(matches.filter(match => match._id !== matchId));
      })
      .catch(error => {
        console.error('Error deleting match:', error);
      });
  };

  return (
    <div className="admin-container">
      <h2>Manage Matches</h2>
      <div className="match-form">
        <input type="text" name="name" placeholder="Match Name" value={editingMatch ? editingMatch.name : newMatch.name} onChange={handleInputChange} />
        <input type="text" name="home_team_country" placeholder="Home Team" value={editingMatch ? editingMatch.home_team_country : newMatch.home_team_country} onChange={handleInputChange} />
        <input type="text" name="away_team_country" placeholder="Away Team" value={editingMatch ? editingMatch.away_team_country : newMatch.away_team_country} onChange={handleInputChange} />
        <input type="datetime-local" name="date" placeholder="Date" value={editingMatch ? editingMatch.date : newMatch.date} onChange={handleInputChange} />
        <input type="text" name="stade" placeholder="Stadium ID" value={editingMatch ? editingMatch.stade : newMatch.stade} onChange={handleInputChange} />
        <button onClick={handleSave}>{editingMatch ? 'Update Match' : 'Create Match'}</button>
      </div>
      <ul className="match-list">
        {matches.map(match => (
          <li key={match._id}>
            <strong>{match.name}</strong> - {match.home_team_country} vs {match.away_team_country} on {new Date(match.date).toLocaleString()} at {match.stade.name}
            <button onClick={() => handleEdit(match)}>Edit</button>
            <button onClick={() => handleDelete(match._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminMatches;
