import React, { useState } from "react";
import "./css/Login.css";
import { Link } from 'react-router-dom';
//import  ForgotPassword  from  "./forgot-password";
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
    
    const [errorMessage, setErrorMessage] = useState("");
    const [error, setError] = useState("");
    const navigate = useNavigate();
    const [values, setValues] = useState({
        username: "",
        passport:""
  
    });
    

    const handleInput = (event) => {
        setValues(prev => ({...prev, [event.target.name]: event.target.value }));
        
    };
   

    const handleSubmit = (event) => {
        event.preventDefault();
       // const errors = Validation(values); // Assumed function for validation
    
        
            axios.post('http://localhost:5000/api/users/login', values)
                .then(res => {
                    if(res.data.message === "Success" && res.data.token){
                        localStorage.setItem("token",res.data.tokes)
                    navigate('/welcomeScreen');}          
         else if  (res.data.error && res.data.error === "Invalid password")
           { console.log("Valid errors:",res.data.error);
           setErrorMessage("password not correct");}
           else setError("email exist pas");
        })
        .catch(err => {
            console.log("Error:", err);
            // Handle other errors, such as network errors
        });
    }
    
        
    

    return (
        <div className='login-container'>
            
            <form className='login-form' onSubmit={handleSubmit}>
                <h2>Sign In</h2>
                <div className='form-group'>
                    <label htmlFor="username">Username</label>
                    <input
                        name='username'
                        type="txt"
                        placeholder="Enter your Username"
                        onChange={handleInput}
                       
                    />
                   {error && <p className="text-danger">{error}</p>}
                </div>
                <div className='form-group'>
                    <label htmlFor="password">Password</label>
                    <input
                        name='password'
                        type="password"
                        placeholder="Enter your password"
                        onChange={handleInput}
                        
                    />
                     {errorMessage && <p className="text-danger">{errorMessage}</p>}
                    <Link to="/ForgotPassword">Forgot Password?</Link>
                </div>
                
                <button type="submit">Login</button>
                <p>
                    Don't have an account?
                    <Link to="/singup" className="FORM-LINK ">Create Account</Link>
                </p>
            </form>
        </div>
    );
}

export default Login;
