import React from 'react';

import QRCode from 'react-qr-code';

function WorldCupTicket({ match, category, price, quantity }) {
    const totalPrice = price * quantity;

    return (
        <div className="ticket">
            <h3>{match}</h3>
            <p>Cat√©gorie:1</p>
            <p>Prix unitaire: 30$</p>
            
            <p>Total: ${totalPrice}</p>
            <QRCode value={`Match: ${match}, Category: ${category}, Price: ${price}, Quantity: ${quantity}, Total: ${totalPrice}`} />
        </div>
    );
}

export default WorldCupTicket;
