import React from 'react';
import { useLocation, Link } from 'react-router-dom';

function Confirmation() {
    const location = useLocation();
    const { match, selectedTickets } = location.state;

    return (
        <div className="container">
            <h2>Confirmation for {match.home_team_country} vs {match.away_team_country}</h2>
            <h2>Stadium</h2>
            <img src={`${match.stade.path}`} alt="Stadium" />
            <div className="confirmed-tickets">
                <h2>Confirmed Tickets</h2>
                <table>
                    <thead>
                        <tr>
                            <th>Category</th>
                            <th>Price</th>
                            <th>Quantity</th>
                            <th>Subtotal</th>
                        </tr>
                    </thead>
                    <tbody>
                        {selectedTickets.map((ticket, index) => (
                            <tr key={index}>
                                <td>{ticket.category}</td>
                                <td>${ticket.price}</td>
                                <td>{ticket.quantity}</td>
                                <td>${ticket.subtotal}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <Link to="/" className="home-button">Back to Home</Link>
        </div>
    );
}

export default Confirmation;
